<script setup lang="ts">
import { onMounted, onUnmounted, ref, computed } from 'vue';
import { useRouter } from 'vue-router';
import { useMaintenanceStore } from '../store/maintenance';

import type { Ref } from 'vue';
const router = useRouter();
const maintenanceStore = useMaintenanceStore();
let pollInterval: ReturnType<typeof setInterval> | undefined;
const audioRef: Ref<HTMLAudioElement | null> = ref(null);

const maintenanceTitle = computed(() => {
  return maintenanceStore.maintenanceType === 'extraordinary' 
    ? 'Unexpected Maintenance in Progress'
    : 'Scheduled Maintenance';
});

const maintenanceMessage = computed(() => {
  if (maintenanceStore.maintenanceType === 'extraordinary') {
    return "We're fixing an unexpected issueâ€”our servers needed a quick coffee break! Sorry for the inconvenience and thanks for your patience. We'll be back online as soon as we've sorted things out.";
  }
  return "We're performing scheduled maintenance to improve your experience. We'll be back shortly with enhanced performance and features. Thank you for your patience!";
});

onMounted(() => {
  // Play music when maintenance page is mounted
  if (audioRef.value) {
    audioRef.value.volume = 0.3; // Set volume as desired
    audioRef.value.play?.().catch(() => {}); // Prevent error if autoplay is blocked
  }
  // Check maintenance status every 30 seconds
  pollInterval = setInterval(async () => {
    try {
      await maintenanceStore.checkMaintenanceStatus();
      if (!maintenanceStore.isUnderMaintenance) {
        router.push({ name: 'Login' });
      }
    } catch (error) {
      console.error('Error checking maintenance status:', error);
    }
  }, 30000);
});

onUnmounted(() => {
  // Pause music when leaving maintenance page
  if (audioRef.value) {
    audioRef.value.pause?.();
    audioRef.value.currentTime = 0;
  }
  if (pollInterval) {
    clearInterval(pollInterval);
  }
});
</script>
  
